    {% set max_batch_size = get_batch_size() %}
    {% set calculated_batch = (2098 / num_columns)|int %}
    {% set batch_size = [max_batch_size, calculated_batch] | min %}

    {{ return(batch_size) }}
{%  endmacro %}

